mapper.select:
SELECT record.*,goods.`name` goodsname,storage.`name` storagename,goodstype.name goodstypename,
(SELECT u.name FROM USER u WHERE u.id=record.`userID`)username,
(SELECT u.name FROM USER u WHERE u.id=record.`admin_id`)adminname
FROM record,goods,STORAGE,goodstype

    record.`goods`=goods.`id`
    and goods.goodsType=goodstype.id
    and goods.storage=storage.id

    and goods.`goodsType`=#{goodstype}


    and goods.`storage`=#{storage1}


    and  goods.`name` like #{name}


limit #{pageNum}, #{pageSize}

select count(*)
from record,goods,goodstype,storage

    record.`goods`=goods.`id`
    and goods.goodsType=goodstype.id
    and goods.storage=storage.id

        and goods.`goodsType`=#{goodstype}


        and goods.`storage`=#{storage1}


        and  goods.`name` like #{name}

SELECT record.*,goods.`name` goodsname,storage.`name` storagename,goodstype.name goodstypename,
(SELECT u.name FROM USER u WHERE u.id=record.`userID`)username,
(SELECT u.name FROM USER u WHERE u.id=record.`admin_id`)adminname
FROM record,goods,STORAGE,goodstype,user

    record.`goods`=goods.`id`
    and goods.goodsType=goodstype.id
    and goods.storage=storage.id
    and user.id=userId

        and goods.`goodsType`=#{goodstype}


        and goods.`storage`=#{storage1}


        and  goods.`name` like #{name}


limit #{pageNum}, #{pageSize}

select count(*)
from record,goods,goodstype,storage,user

    record.`goods`=goods.`id`
    and goods.goodsType=goodstype.id
    and goods.storage=storage.id
    and user.id=userId

        and goods.`goodsType`=#{goodstype}


        and goods.`storage`=#{storage1}


        and  goods.`name` like #{name}
